contracts/Relayer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {MerkleTree, IHasher} from "./MerkleTree.sol";
import {ERC20} from "solmate/tokens/ERC20.sol";
import {Vault} from "./Vault.sol";
import {IDepositVerifier, IWithdrawVerifier, ISwapVerifier, IFinalizeVerifier} from "./IVerifier.sol";

struct ModelOutput {
    uint8 direction;
    uint248 amount;
}

struct ModelInput {
    uint256 chainlinkPrice;
}

enum NodeStatus {
    EMPTY,
    TRANSACTED,
    NULLIFIED
}

struct TxResult {
    uint8 direction;
    uint120 amountA;
    uint120 amountB;
}

contract Relayer is MerkleTree {

    ERC20 immutable public TOKEN_A;
    ERC20 immutable public TOKEN_B;
    IDepositVerifier public depositVerifier;
    IWithdrawVerifier public withdrawVerifier;
    ISwapVerifier public swapVerifier;
    IFinalizeVerifier public finalizeVerifier;
    ModelInput public modelInput;
    mapping(bytes32 => NodeStatus) public nodeStatusPool;
    mapping(bytes32 => TxResult) public transactionResults;

    constructor(
        ERC20 _tokenA,
        ERC20 _tokenB,
        IDepositVerifier _depositVerifier,
        IWithdrawVerifier _withdrawVerifier,
        ISwapVerifier _swapVerifier,
        IFinalizeVerifier _finalizeVerifier,
        IHasher _hasher
    ) MerkleTree(32, _hasher) {
        TOKEN_A = _tokenA;
        TOKEN_B = _tokenB;
        depositVerifier = _depositVerifier;
        withdrawVerifier = _withdrawVerifier;
        swapVerifier = _swapVerifier;
        finalizeVerifier = _finalizeVerifier;
    }

    function deposit(
        bytes32 _cNode,
        uint256 _balanceA,
        uint256 _balanceB,
        address _vault,
        bytes calldata _proof
    ) public returns (uint32) {
        uint256[5] memory _publicInputs;
        _publicInputs[0] = uint256(_cNode);
        _publicInputs[1] = uint256(Vault(_vault).cModel());
        _publicInputs[2] = uint256(_balanceA);
        _publicInputs[3] = uint256(_balanceB);
        _publicInputs[4] = uint256(uint160(_vault));
        require(
            msg.sender == _vault,
            "Deposit not trader"
        );
        require(
            depositVerifier.verify(_publicInputs, _proof),
            "Deposit Verifier failed"
        );

        TOKEN_A.transferFrom(_vault, address(this), _balanceA);
        TOKEN_B.transferFrom(_vault, address(this), _balanceB);

        return MerkleTree._insert(_cNode);
    }

    function withdraw(
        bytes32 _nullifier,
        uint256 _balanceA,
        uint256 _balanceB,
        address _vault,
        bytes calldata _proof
    ) public {
        uint256[5] memory _publicInputs;
        _publicInputs[0] = uint256(root);
        _publicInputs[1] = uint256(_nullifier);
        _publicInputs[2] = uint256(_balanceA);
        _publicInputs[3] = uint256(_balanceB);
        _publicInputs[4] = uint256(uint160(_vault));
        require(
            msg.sender == _vault,
            "Withdraw not trader"
        );
        require(
            withdrawVerifier.verify(_publicInputs, _proof),
            "Withdraw Verifier failed"
        );

        TOKEN_A.transferFrom(address(this), _vault, _balanceA);
        TOKEN_B.transferFrom(address(this), _vault, _balanceB);

        nodeStatusPool[_nullifier] = NodeStatus.NULLIFIED;
    }

    function transact(
        bytes32 _nullifier,
        ModelOutput calldata modelOutput,
        bytes calldata _proof
    ) public {
        uint256[4] memory _publicInputs;
        _publicInputs[0] = uint256(MerkleTree.root);
        _publicInputs[1] = uint256(_nullifier);
        _publicInputs[2] = modelInput.chainlinkPrice;
        _publicInputs[3] = uint256(bytes32(abi.encodePacked(modelOutput.direction, modelOutput.amount)));
        require(
            swapVerifier.verify(_publicInputs, _proof),
            "Swap Verifier failed"
        );

        transactionResults[_nullifier] = _transact(modelOutput);
        nodeStatusPool[_nullifier] = NodeStatus.TRANSACTED;
    }

    function finalize(
        bytes32 _nullifier,
        bytes32 _cNode2,
        bytes calldata _proof
    ) public {
        uint256[3] memory _publicInputs;
        _publicInputs[0] = uint256(MerkleTree.root);
        _publicInputs[1] = uint256(_nullifier);
        _publicInputs[2] = uint256(_cNode2);
        require(
            finalizeVerifier.verify(_publicInputs, _proof),
            "finalize: verify failed"
        );

        MerkleTree._insert(_cNode2);
        delete transactionResults[_nullifier];
        nodeStatusPool[_nullifier] = NodeStatus.NULLIFIED;
    }

    function _transact(ModelOutput calldata _modelOutput) private pure returns (TxResult memory) {
        // TODO swap with 1inch aggregator
        return TxResult(0, 0, 0);
    }
}

contracts/Vault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;
import {ERC20} from "solmate/tokens/ERC20.sol";
import {Owned} from "solmate/auth/Owned.sol";

interface IRelayer {
    function deposit(
        bytes32 _cNode,
        bytes32 _cModel,
        uint256 _balanceA,
        uint256 _balanceB,
        bytes calldata _proof
    ) external returns (uint32);

    function withdraw(
        bytes32 _nullifier,
        uint256 _balanceA,
        uint256 _balanceB,
        address _vault,
        bytes calldata _proof
    ) external;

    function TOKEN_A() external returns (ERC20);
    function TOKEN_B() external returns (ERC20);
}

enum VaultState {
    DEPOSIT,
    WAITING,
    WITHDRAW
}

contract Vault is Owned {

    IRelayer immutable public relayer;
    bytes32 public cModel;
    mapping(address => uint256) public userWeights;
    uint256 public totalWeights;
    uint256 public afterBalance;
    VaultState public state;

    constructor(
        IRelayer _relayer,
        address _trader,
        bytes32 _cModel
    ) Owned(_trader) {
        relayer = _relayer;
        cModel = _cModel;
        relayer.TOKEN_A().approve(address(relayer), type(uint256).max);
        relayer.TOKEN_B().approve(address(relayer), type(uint256).max);
    }

    modifier requireState(VaultState _state) {
        require(state == _state, "Invalid Vault state");
        _;
    }

    function userDeposit(uint256 amount) public requireState(VaultState.DEPOSIT) {
        relayer.TOKEN_A().transferFrom(msg.sender, address(this), amount);
        userWeights[msg.sender] += amount;
        totalWeights += amount;
    }

    function depositToRelayer(
        bytes32 _cNode,
        bytes calldata _proof
    ) public requireState(VaultState.DEPOSIT) onlyOwner {
        // TODO swap A to B and make 50 50
        uint256 balanceA = relayer.TOKEN_A().balanceOf(address(this));
        uint256 balanceB = relayer.TOKEN_B().balanceOf(address(this));
        relayer.deposit(_cNode, cModel, balanceA, balanceB, _proof);
        state = VaultState.WAITING;
    }

    function withdrawFromRelayer(
        bytes32 _nullifier,
        uint256 _balanceA,
        uint256 _balanceB,
        bytes calldata _proof
    ) public requireState(VaultState.WAITING) onlyOwner {
        relayer.withdraw(_nullifier, _balanceA, _balanceB, address(this), _proof);
        // TODO swap B to A
        afterBalance = relayer.TOKEN_A().balanceOf(address(this));
        state = VaultState.WITHDRAW;
    }

    function userWithdraw(address to) public requireState(VaultState.WITHDRAW) {       
        uint256 amount = afterBalance * userWeights[to] / totalWeights;
        relayer.TOKEN_A().transferFrom(address(this), to, amount);
    }
}

